/* ---------------------------------------------------------------------


     
procedure recla_desde_mayor_adl;

var     tmpdate :       date;
        cdate   :       char;
        c2date  :       char;
        no_mths :       number;
        FC      :       CHAR;
        PC      :       CHAR;
        FCID    :       DATE;
        PCDE    :       DATE;   
        fch_emision:    date;
        open_id :       number;
        status_id:      number;
        LAST_KEY:       NUMBER(9,0);

set_trace();

begin


    LAST_KEY := GET_CMD();

    
    IF (LAST_KEY = FPREV) AND (REFERIDO_RECLA_FR.ACCION <> 'RREC')
    THEN
        SET_CURRENT_FIELD(REFERIDO_RECLA_FR.FECHA_IDENTIFICADO);
    {ENDIF}

    if REFERIDO_RECLA_FR.COD_SERVICIO0 = 'AE-C'
    then
        begin
           return;
        end;
    {ENDIF}
    if (to_char(REFERIDO_RECLA_FR.DESDE) = '')  THEN
        begin
                SET_FAILURE();
                RETURN;
        end;  

DB_open(GENERAL_TBL_DSD,OPEN_ID, STATUS_ID);
DB_RESET(OPEN_ID, STATUS_ID);
GENERAL_TBL_DSD.COD_GENERAL := 'DATE';
IF SUBSTR(REFERIDO_RECLA_FR.COD_SERVICIO0,0,3) = 'PAN' THEN
        GENERAL_TBL_DSD.COD_ID := 'EPAN';
ELSE
        IF SUBSTR(REFERIDO_RECLA_FR.COD_SERVICIO0,0,2) = 'AE' THEN
                GENERAL_TBL_DSD.COD_ID := 'EAE';
DB_QUERY(OPEN_ID, STATUS_ID);
DB_GET_FIRST(OPEN_ID, STATUS_ID);
IF STATUS_ID = 0 THEN
        FCH_EMISION := TO_DATE(SUBSTR(GENERAL_TBL_DSD.DSC_CORTA,0,2) || '-' ||
                                SUBSTR(GENERAL_TBL_DSD.DSC_CORTA,2,3) || '-'||
                                SUBSTR(GENERAL_TBL_DSD.DSC_CORTA,5,4));
ELSE
        FCH_EMISION :=  SYSTEM_DATE;

if  FCH_EMISION  < REFERIDO_RECLA_FR.DESDE
then
begin
error(7575);
set_failure();
return;
end;
DB_close(OPEN_ID, STATUS_ID);


end;

set_notrace();
    



--------------------------------------------------------------------- */
