  /**
   *
   */
  protected void step1 ( ParameterSet ps )
  {
	  GASTOS_NUCLEO_RO_DSD = this.dm.create ( "GASTOS_NUCLEO_RO_DSD" );
	 
	  OPEN_ID = GASTOS_NUCLEO_RO_DSD.getId ( );
	  STATUS_ID = RallyDataStoreException.getDefault ( );
	  EXENC_TRABAJO_FLG = "N"; 
      STATUS_ID  = this.dm.reset ( OPEN_ID );

      this.dm.setField ( this.dm.get ( "GASTOS_NUCLEO_RO_DSD" ), "SEGURO_SOCIAL_JEFE_FAM", this.app.SS_JEFE_GBL );
      this.dm.setField ( this.dm.get ( "GASTOS_NUCLEO_RO_DSD" ), "SEGURO_SOCIAL_JEFE_SERV", this.app.SS_SERV_GBL );
      this.dm.setField ( this.dm.get ( "GASTOS_NUCLEO_RO_DSD" ), "COD_SERVICIO", this.app.COD_SERV_GBL );
 
      STATUS_ID  = this.dm.limitQueryFromBuffer ( OPEN_ID );
      STATUS_ID  = this.dm.retrieve ( OPEN_ID );
      STATUS_ID  = this.dm.firstRow ( OPEN_ID );

      while ( Compare.equals ( STATUS_ID, 0 ) && Compare.equals (EXENC_TRABAJO_FLG , "N"  ))
      {
          if ( ( Compare.equals ( this.dm.getFieldString ( this.dm.get ( "GASTOS_NUCLEO_RO_DSD" ), "COD_TIPO_DEDUCCIONE" ), "E075" ) ) || ( Compare.equals ( this.dm.getFieldString ( this.dm.get ( "GASTOS_NUCLEO_RO_DSD" ), "COD_TIPO_DEDUCCIONE" ), "E100" ) ) || ( Compare.equals ( this.dm.getFieldString ( this.dm.get ( "GASTOS_NUCLEO_RO_DSD" ), "COD_TIPO_DEDUCCIONE" ), "ETYT" ) ) || ( Compare.equals ( this.dm.getFieldString ( this.dm.get ( "GASTOS_NUCLEO_RO_DSD" ), "COD_TIPO_DEDUCCIONE" ), "ETRT" ) ) ) 
          {      	    
        	  EXENC_TRABAJO_FLG = "S";
          }
          STATUS_ID  = this.dm.nextRow ( OPEN_ID );
      }
      
      if ( ( Compare.equals ( this.app.COD_SERV_GBL, "AE-C" ) ) && ( Compare.gt ( this.app.form ( "FECHAS_SERVICIO_FRM" ).getDoubleItem ( "NUM_MESES" ), 3 ) ) && Compare.equals (EXENC_TRABAJO_FLG , 'S'  ) ) 
      {
        this.app.setError ( 0, 2779 );
        this.app.errorFlag = false;
      }
      if ( ( Compare.equals ( this.app.COD_SERV_GBL, "AE-C" ) ) && ( Compare.gt ( this.app.form ( "FECHAS_SERVICIO_FRM" ).getDoubleItem ( "NUM_MESES" ), 6 ) )  ) 
    {
      this.app.setError ( 0, 2762 );
      this.app.errorFlag = false;
    }
    if ( ( Compare.equals ( this.app.COD_SERV_GBL, "AE-D" ) ) && ( Compare.gt ( this.app.form ( "FECHAS_SERVICIO_FRM" ).getDoubleItem ( "NUM_MESES" ), 12 ) )  ) 
    {
      this.app.setError ( 1, 2771 );
      this.app.errorFlag = false;
    }
    if ( ( Compare.equals ( this.app.COD_SERV_GBL, "PAN" ) ) && ( Compare.ge ( this.app.form ( "FECHAS_SERVICIO_FRM" ).getDoubleItem ( "NUM_MESES" ), 24 ) )  ) 
    {

    	CLIENTES2_DSD = this.dm.create ( "CLIENTES2_DSD" );

    	OPEN3_ID = CLIENTES2_DSD.getId ( );
    	STATUS3_ID = RallyDataStoreException.getDefault ( );
    	STATUS3_ID  = this.dm.reset ( OPEN3_ID );

    	this.dm.setField ( this.dm.get ( "CLIENTES2_DSD" ), "SEGURO_SOCIAL_CLIENTE", this.app.form ( "FECHAS_SERVICIO_FRM" ).getDoubleItem ( "SEGURO_SOCIAL_JEFE_SERV" ) );

    	STATUS3_ID  = this.dm.limitQueryFromBuffer ( OPEN3_ID );
    	STATUS3_ID  = this.dm.retrieve ( OPEN3_ID );
    	STATUS3_ID  = this.dm.firstRow ( OPEN3_ID );

    	if ( Compare.equals ( STATUS3_ID, 0 ))
    	{
    		EDAD = DateUtil.monthsBetween ( this.app.SYSTEM_DATE, this.dm.getFieldDate ( this.dm.get ( "CLIENTES2_DSD" ), "FCH_NACIMIENTO" ) ) / 12;
    		if  ( Compare.lt(  EDAD, 60 ) )  
    		{
    			if (( Compare.equals ( this.dm.getFieldString ( this.dm.get ( "CLIENTES2_DSD" ), "FLG_INCAPACIDAD_VERIFICADA" ),"N") ))
    			{
    				this.app.setError ( "0", "Cliente debe ser mayor de 60 o con una incapacidad verificada para esta cantidad de meses." );
    				this.app.errorFlag = false;
    				this.doReturn ( );
    				return;
    			}
    		}
    	}
    }
    return;
  }
