/* ---------------------------------------------------------------------


     
PROCEDURE RECOFI_A_NOMBRE_ADL;

VAR
        A_ECON          :       CHAR(9);
        UNI             :       CHAR(1) EXPORT;
        DEC             :       CHAR(1) EXPORT;
        CEN             :       CHAR(1) EXPORT;
        MIL             :       CHAR(1) EXPORT;
        BENE            :       CHAR(8);
        LARGO           :       NUMBER;
        CANT_PALABRA    :       CHAR(55);

SET_TRACE ();

CALL ANO_ECONOMICO_ADL(RECOFI_A_FR.RECAUDACIONES_FCH_PAGO,A_ECON);

RECOFI_A_FR.A_ECONO     :=      A_ECON;

{ CANTIDAD EN PALABRAS }

        BENE := TO_CHAR(TRUNC(RECOFI_A_FR.RECAUDACIONES_VLR_CANTIDAD));
        LARGO := LENGTH(BENE);
        IF LARGO = 4
        THEN
          BEGIN
            MIL := SUBSTR(BENE,0,1);
            CEN := SUBSTR(BENE,1,1);
            DEC := SUBSTR(BENE,2,1);
            UNI := SUBSTR(BENE,3,1);
          END;
        ELSE
           IF LARGO = 3
           THEN
           BEGIN
            MIL := '0';
            CEN := SUBSTR(BENE,0,1);
            DEC := SUBSTR(BENE,1,1);
            UNI := SUBSTR(BENE,2,1);
           END;
           ELSE
              IF LARGO = 2
              THEN
                BEGIN
                MIL := '0';
                CEN := '0';
                DEC := SUBSTR(BENE,0,1);
                UNI := SUBSTR(BENE,1,1);
                END;
              ELSE
                 IF LARGO = 1
                 THEN
                  BEGIN
                    MIL := '0';
                    CEN := '0';
                    DEC := '0';
                    UNI := SUBSTR(BENE,0,1);
                  END;

        DEBUG_LOG(MIL||'-'||CEN||'-'||DEC||'-'||UNI);

        CALL CAMBIO_A_PALABRA(MIL,CEN,DEC,UNI,CANT_PALABRA);

        RECOFI_A_FR.SC7652_CAMBIO_PALABRA  := CANT_PALABRA;

CALL BUSCAR_NOMBRE_COMPLETO_ADL
(RECOFI_A_FR.RECAUDACIONES_SEGURO_SOCIAL_JEFE_SERV,RECOFI_A_FR.SC7652_NOMBRE);

IF RECOFI_A_FR.RECAUDACIONES_MODO_PAGO = 'CHEQ' THEN
        RECOFI_A_FR.SC7652_CHEQ := 'XX';
ELSE
IF RECOFI_A_FR.RECAUDACIONES_MODO_PAGO = 'GIRO' THEN
        RECOFI_A_FR.SC7652_GIRO := 'XX';
ELSE
        RECOFI_A_FR.SC7652_EFEC := 'XX';

CALL RECOFI_PRN_MENU;

SET_NOTRACE ();
    



--------------------------------------------------------------------- */
