  /**
   *
   */
  protected void step0 ( ParameterSet ps )
  {

    NGET_CMD = RallyAppCommandFactory.translate ( this.app.getLastCommandName ( ) );
    PTRA_EMPLEOS2_DSD = this.dm.create ( "PTRA_EMPLEOS2_DSD" );
    PTRA_EMPLEO_TABLE = PTRA_EMPLEOS2_DSD.getId ( );
    PTRA_EMPLEO_STATUS = RallyDataStoreException.getDefault ( );

    PTRA_EMPLEO_STATUS  = this.dm.reset ( PTRA_EMPLEO_TABLE );

    PTRA_TBL_RO_DSD = this.dm.create ( "PTRA_TBL_RO_DSD" );
    PTRA_TABLE = PTRA_TBL_RO_DSD.getId ( );
    PTRA_STATUS = RallyDataStoreException.getDefault ( );

    PTRA_STATUS  = this.dm.reset ( PTRA_TABLE );

    this.dm.setField ( this.dm.get ( "PTRA_EMPLEOS2_DSD" ), "SEGURO_SOCIAL_CLIENTE", this.app.form ( "PTRA_CLIENTE_FR" ).getDoubleItem ( "SEGURO_SOCIAL_CLIENTE" ));
    this.dm.setField ( this.dm.get ( "PTRA_EMPLEOS2_DSD" ), "RMO_OFICINA", this.app.RMO_GBL );
    this.dm.setField ( this.dm.get ( "PTRA_EMPLEOS2_DSD" ), "EMPLEO_SECUENCIA",this.app.form ( "PTRA_CLIENTE_FR" ).getDoubleItem ("NUM_EMPLEO") );

    PTRA_EMPLEO_STATUS  = this.dm.limitQueryFromBuffer ( PTRA_EMPLEO_TABLE );
    PTRA_EMPLEO_STATUS  = this.dm.retrieve ( PTRA_EMPLEO_TABLE );

    PTRA_EMPLEO_STATUS  = this.dm.firstRow ( PTRA_EMPLEO_TABLE );

    if ( Compare.equals ( PTRA_EMPLEO_STATUS, 0 ) ) 
    {
      FCH_INICIO = this.dm.getFieldDate ( this.dm.get ( "PTRA_EMPLEOS2_DSD" ), "FECHA_INICIO" );
    }
    else
    {
      FCH_INICIO = this.app.SYSTEM_DATE;
    }

    PTRA_STATUS  = this.dm.limitQueryFromBuffer ( PTRA_TABLE );
    PTRA_STATUS  = this.dm.retrieve ( PTRA_TABLE );

    PTRA_STATUS  = this.dm.firstRow ( PTRA_TABLE );

    NUM_MESES_EXEN = 0;
    if ( Compare.gt ( this.app.form ( "PTRA_CLIENTE_FR" ).getDateItem ( "FCH_COBRO" ), RallySpanishDatesParser.parseDateString ( "" ) ) ) 
    {
      while ( ( Compare.equals ( PTRA_STATUS, 0 ) ) && ( Compare.le ( this.dm.getFieldDate ( this.dm.get ( "PTRA_TBL_RO_DSD" ), "FCH_EFECTIVIDAD" ), FCH_INICIO ) )   )
      {
        NUM_MESES_EXEN = this.dm.getFieldDouble ( this.dm.get ( "PTRA_TBL_RO_DSD" ), "TIEMPO_EXENCION" );

        PTRA_STATUS  = this.dm.nextRow ( PTRA_TABLE );

      }
    }
    PTRA_STATUS  = this.dm.dbClose ( PTRA_TABLE );

    PTRA_EMPLEO_STATUS  = this.dm.dbClose ( PTRA_EMPLEO_TABLE );

    UPDATE_CONFIRM = 0;
    this.app.NUM_EMPLEO_GBL = this.app.form ( "PTRA_CLIENTE_FR" ).getDoubleItem ( "NUM_EMPLEO" );
    this.app.FLG_FCH_COBRO_GBL = "N";
    this.app.FLG_FCH_TERMINA_GBL = "N";
    if ( Compare.equals ( this.app.NUM_EMPLEO_GBL, 1 ) ) 
    {
      this._condition0 = true;
    }
    else
    {
      this._condition0 = false;
    }
    if ( this._condition0 )
    {
      //this.app.callCommand ( RallyAppCommandFactory.QBE );
      return;
    }

  }


