  /**
   * to get the tecnico's COD_SEGURIDAD
   */
  protected void step0 ( ParameterSet ps )
  {
    TECNICO_READ_DSD = this.dm.create ( "TECNICO_READ_DSD" );
    TEC_ID = TECNICO_READ_DSD.getId ( );
    STAT_CODE = RallyDataStoreException.getDefault ( );
    if ( ! Compare.equals ( STAT_CODE, 0 ) ) 
    {
      this.app.setError ( 0, STAT_CODE );

      STAT_CODE  = this.dm.rollBack ( );
      this.doReturn ( );
      return; 
    }

    STAT_CODE  = this.dm.reset ( TEC_ID );

    this.tss = (new BigDecimal(Double.toString(this.app.form ( "TSS_PRINT_CITAS_PRE_FR" ).getDoubleItem ( "TECNICO_VAR" )))).toPlainString();
    this.dm.setField ( this.dm.get ( "TECNICO_READ_DSD" ), "ID_TECNICO", this.tss );

    STAT_CODE  = this.dm.limitQueryFromBuffer ( TEC_ID );
    STAT_CODE  = this.dm.retrieve ( TEC_ID );

    if ( ! Compare.equals ( STAT_CODE, 0 ) ) 
    {
      this.app.setError ( 1, STAT_CODE );

      STAT_CODE  = this.dm.rollBack ( );
      this.doReturn ( );
      return; 
    }

    STAT_CODE  = this.dm.firstRow ( TEC_ID );

    if ( Compare.equals ( STAT_CODE, 0 ) ) 
    {
      codSeguridad = (String)this.dm.getField ( this.dm.get ( "TECNICO_READ_DSD" ), "COD_SEGURIDAD" );
    }
    else
    {
  	  this.app.setError("-1", "No se pudo acceder Base de Datos de SAIC.");
      STAT_CODE  = this.dm.dbClose ( TEC_ID );
      this.doReturn ( );
      return;
    }
    STAT_CODE  = this.dm.dbClose ( TEC_ID );
    return;
  }

  /**
   *
   */
  protected void step1 ( ParameterSet ps )
  {
	try
	{
	  service = new WsPeriodos();
	}
	catch (WebServiceException ex) {
	  System.out.print(ex.toString());
	  this.app.setError("-1", "No se pudo acceder el sistema SIBIF.");
	  this.doReturn();
	  return;
	}
	//this.tss = (new BigDecimal(Double.toString(this.app.ID_TECNICO_GBL))).toPlainString();
    //this.tss = (new BigDecimal(Double.toString(this.app.form ( "TSS_PRINT_CITAS_PRE_FR" ).getDoubleItem ( "TECNICO_VAR" )))).toPlainString();
    Date FECHAS_VENCE = this.app.form ( "TSS_PRINT_CITAS_PRE_FR" ).getDateItem ( "INPUT_START_DATE" );
    //Date FECHAS_VENCE2 = MathService.plus ( TruncService.lastDay ( this.app.form ( "TSS_PRINT_CITAS_PRE_FR" ).getDateItem ( "INPUT_END_DATE" )) , 1  );
    Date FECHAS_VENCE2 = this.app.form ( "TSS_PRINT_CITAS_PRE_FR" ).getDateItem ( "INPUT_END_DATE" );
    DateFormat df = new SimpleDateFormat("MM/dd/yyyy");
    String dateFROM = df.format(FECHAS_VENCE);
    String dateTO = df.format(FECHAS_VENCE2);
    WsPeriodosSoap soap = service.getWsPeriodosSoap();
	//citasObj = soap.buscarPeriodos(codSeguridad, null, FECHAS_VENCE.toString(), FECHAS_VENCE2.toString());
	//citasObj = soap.buscarPeriodos(codSeguridad, null, "8/1/2011", "9/1/2011");
	citasObj = soap.buscarPeriodos(codSeguridad, null, dateFROM, dateTO);
	if ( Compare.equals( this.citasObj, null ) )
	{
	  this.app.setError("-1", "No hay citas pautadas.");
	  this.doReturn();
	  return;
    }
	return;
  }

  protected void step2 ( ParameterSet ps )
  {
	TECNICO_RO_DSD = this.dm.create ( "TECNICO_RO_DSD" );
	TEC_ID = TECNICO_RO_DSD.getId ( );
	STAT_CODE = RallyDataStoreException.getDefault ( );
	CALENDARIO2_DSD = this.dm.create("CALENDARIO2_DSD");
	CALENDARIO2_ID = CALENDARIO2_DSD.getId();
	CALENDARIO2_STATUS_CODE = RallyDataStoreException.getDefault ( );
	// retrieve periods from SIBIF
	Iterator<PeriodosCitas> citasIter = citasObj.getCitasPeriodos().getPeriodosCitas().iterator();
	// loop through received periods
	for (; citasIter.hasNext();)
	{
	  PeriodosCitas cita = citasIter.next();
	  try
	  {
		startDate = formatter.parse( cita.getHoraComienzo() );
		endDate = formatter.parse( cita.getHoraFinalizacion());
	  } catch (ParseException e)
	  {
		System.out.print(e.toString());
		e.printStackTrace();
	  }
	  long elapsed = endDate.getTime() - startDate.getTime();
	  DateFormat df = new SimpleDateFormat("HHmm");
	  Calendar cal = Calendar.getInstance();
	  int year=cal.get(Calendar.YEAR);
	  df.setTimeZone(TimeZone.getTimeZone("GMT"));
	  cal.setTime(new Date(elapsed));
	  String elapsedHHMM = df.format(cal.getTime());
	  String FECHA_CHAR = "0101" + Integer.toString(year) + elapsedHHMM;
	  STAT_CODE  = this.dm.reset ( TEC_ID );
	  this.dm.setField ( this.dm.get ( "TECNICO_RO_DSD" ), "COD_SEGURIDAD", cita.getUsuarioID() );
	  STAT_CODE  = this.dm.limitQueryFromBuffer ( TEC_ID );
	  STAT_CODE  = this.dm.retrieve ( TEC_ID );
	  if ( ! Compare.equals ( STAT_CODE, 0 ) ) 
	  {
	  	this.app.setError("-1", "No se encontraron los datos del técnico en SAICWEB.");
		this.doReturn();
		return;
	  }
	  String numSS = cita.getCodigoTransaccion();
	  numSS = removeChar(numSS, '-' );
	  double tecnicoID = 0.0;
	  double rmo = this.app.RMO_GBL;
	  STAT_CODE  = this.dm.firstRow ( TEC_ID );
	  if ( Compare.equals ( STAT_CODE, 0 ) ) 
	  {
	    tecnicoID = this.dm.getFieldDouble(TECNICO_RO_DSD, "ID_TECNICO");
	    CALENDARIO2_STATUS_CODE = this.dm.reset( CALENDARIO2_ID );
	    this.dm.setField ( this.dm.get ( "CALENDARIO2_DSD" ), "ID_TECNICO", tecnicoID );
	    this.dm.setField ( this.dm.get ( "CALENDARIO2_DSD" ), "FCH_ACTIVIDAD_HORA_MIN_CONTAC0", startDate );
	    this.dm.setField ( this.dm.get ( "CALENDARIO2_DSD" ), "RMO_OFICINA", rmo );
	    CALENDARIO2_STATUS_CODE  = this.dm.limitQueryFromBuffer ( CALENDARIO2_ID );
	    CALENDARIO2_STATUS_CODE  = this.dm.retrieve ( CALENDARIO2_ID );
	    CALENDARIO2_STATUS_CODE  = this.dm.firstRow ( CALENDARIO2_ID );
	    if ( ! Compare.equals ( CALENDARIO2_STATUS_CODE, 0 ) ) 
	    {		
		  this.dm.setField(CALENDARIO2_DSD, "ID_TECNICO", tecnicoID );
		  this.dm.setField(CALENDARIO2_DSD, "COD_SERVICIO", cita.getCodigoActividad() );
		  this.dm.setField(CALENDARIO2_DSD, "SEGURO_SOCIAL_JEFE_FAM", numSS);
		  this.dm.setField(CALENDARIO2_DSD, "SEGURO_SOCIAL_JEFE_SERV", numSS);
		  this.dm.setField(CALENDARIO2_DSD, "DURACION_HORA_MIN_CONTACTO", RallySpanishDatesParser.parseDateString ( FECHA_CHAR, "DDMMYYYYHH24MI" ));
		  this.dm.setField(CALENDARIO2_DSD, "FCH_ACTIVIDAD_HORA_MIN_CONTAC0", startDate);
		  this.dm.setField(CALENDARIO2_DSD, "DSC_EVENTO", cita.getEtapaDescripcion());
		  this.dm.setField(CALENDARIO2_DSD, "COD_RAZON", cita.getCodigoEtapa());
		  this.dm.setField(CALENDARIO2_DSD, "STS_ACTIVIDAD", " ");
		  this.dm.setField(CALENDARIO2_DSD, "RMO_OFICINA", rmo );
		  CALENDARIO2_STATUS_CODE = this.dm.insertRow( CALENDARIO2_ID );
	    }
	    else // update the calendar row with downloaded data 
	    {
		  this.dm.setField(CALENDARIO2_DSD, "ID_TECNICO", tecnicoID);
		  this.dm.setField(CALENDARIO2_DSD, "COD_SERVICIO", cita.getCodigoActividad() );
		  this.dm.setField(CALENDARIO2_DSD, "SEGURO_SOCIAL_JEFE_FAM", numSS);
		  this.dm.setField(CALENDARIO2_DSD, "SEGURO_SOCIAL_JEFE_SERV", numSS);
		  this.dm.setField(CALENDARIO2_DSD, "DURACION_HORA_MIN_CONTACTO", RallySpanishDatesParser.parseDateString ( FECHA_CHAR, "DDMMYYYYHH24MI" ));
		  this.dm.setField(CALENDARIO2_DSD, "FCH_ACTIVIDAD_HORA_MIN_CONTAC0", startDate);
		  this.dm.setField(CALENDARIO2_DSD, "DSC_EVENTO", cita.getEtapaDescripcion());
		  this.dm.setField(CALENDARIO2_DSD, "COD_RAZON", cita.getCodigoEtapa());
		  this.dm.setField(CALENDARIO2_DSD, "STS_ACTIVIDAD", " ");
		  this.dm.setField(CALENDARIO2_DSD, "RMO_OFICINA", rmo );
		  CALENDARIO2_STATUS_CODE = this.dm.update( CALENDARIO2_ID );
	    }
		this.dm.dbCommit();    	
/*	    if(cita.getCodigoTransaccion().length() > 0)
	    {
		  this.app.setSS_JEFE_FAMILIA_GBL ( Double.parseDouble(cita.getCodigoTransaccion()) );
		  this.app.setSS_JEFE_SERVICIO_GBL ( Double.parseDouble(cita.getCodigoTransaccion()) );
		  //TSS_PRINT_CITA_PKT.exec (app);
		  app.enqueue ( "TSS_PRINT_CITA_PKT" );
		  this.app.doQueue ( );
	    }*/
	  }
	}
	CALENDARIO2_STATUS_CODE  = this.dm.dbClose ( CALENDARIO2_ID );
	STAT_CODE  = this.dm.dbClose ( TEC_ID );
	TSS_PRINT_CITAS_PKT.exec (app);
	return;
  }
  
  protected static String removeChar(String s, char c) {
	String r = "";
	for (int i = 0; i < s.length(); i ++) {
	  if (s.charAt(i) != c) r += s.charAt(i);
	}
	return r;
  }

