/* ---------------------------------------------------------------------


     
PROCEDURE AD220B_B_RECIBO_ADL;

VAR
        CATEGORIA       :       CHAR(2);
        UNI             :       CHAR(1) EXPORT;
        DEC             :       CHAR(1) EXPORT;
        CEN             :       CHAR(1) EXPORT;
        MIL             :       CHAR(1) EXPORT;
        BENE            :       CHAR(8);
        LARGO           :       NUMBER;
        CANT_PALABRA    :       CHAR(55);
        css_table       :       number(9,0);
        css_status      :       number(9,0);

SET_TRACE ();

        db_open(SERVICIOS_TBL_RO_DSD,css_table,css_status);
        db_reset(css_table,css_status);
        SERVICIOS_TBL_RO_DSD.cod_servicio := AD220B_A3_FR.SERV;
        db_query(css_table,css_status);
        if css_status <> 0 then
                error(css_status);
        if css_status = 0
        then
                begin
                 db_get_first(css_table,css_status);
                 if css_status <> 0 then
                 error(css_status);
                 reccon :=  SERVICIOS_TBL_RO_DSD.DSC_NOMBRE_SERVICIO;           
    
                end;    
        else
                reccon := '***************';
        db_close(css_table,css_status);


{cambio a palabra}

        BENE := TO_CHAR(TRUNC(AD220B_A3_FR.CANP));        
        LARGO := LENGTH(BENE);
        IF LARGO = 4
        THEN
          BEGIN
            MIL := SUBSTR(BENE,0,1);
            CEN := SUBSTR(BENE,1,1);
            DEC := SUBSTR(BENE,2,1);
            UNI := SUBSTR(BENE,3,1);
          END;
        ELSE
           IF LARGO = 3
           THEN
           BEGIN
            MIL := '0';
            CEN := SUBSTR(BENE,0,1);
            DEC := SUBSTR(BENE,1,1);
            UNI := SUBSTR(BENE,2,1);
           END;
           ELSE
              IF LARGO = 2
              THEN
                BEGIN
                MIL := '0';
                CEN := '0';
                DEC := SUBSTR(BENE,0,1);
                UNI := SUBSTR(BENE,1,1);
                END;
              ELSE
                 IF LARGO = 1
                 THEN
                  BEGIN
                    MIL := '0';
                    CEN := '0';
                    DEC := '0';
                    UNI := SUBSTR(BENE,0,1);
                  END;
                  
        DEBUG_LOG(MIL||'-'||CEN||'-'||DEC||'-'||UNI);
        
        CALL CAMBIO_A_PALABRA(MIL,CEN,DEC,UNI,CANT_PALABRA);
        
        RECCAN := CANT_PALABRA;

SET_NOTRACE();
    



--------------------------------------------------------------------- */
